import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import { Calendar, Activity, TrendingUp, Save, CheckCircle2, Dumbbell, Clock, ChevronRight, ChevronLeft, Zap, Info, Utensils, Coffee, Moon, Sun, ArrowRightLeft, MessageCircle, Sparkles, Loader2, ChefHat } from 'lucide-react';

// --- MODE HORS LIGNE (Configuration locale) ---
const auth = null;
const db = null;
const appId = "local-mode";

// --- CITATIONS DE DAXTER ---
const DAXTER_QUOTES = [
  "Wow ! T'es une vraie bête ! Même les Metal Heads auraient peur de toi !",
  "Allez hop hop hop ! On se bouge le fessier, héros !",
  "C'est tout ce que t'as ? Je suis sûr que tu peux faire mieux !",
  "Joli mouvement ! Presque aussi gracieux que moi... presque.",
  "Hé ! Si tu continues comme ça, tu vas finir par sauver le monde !",
  "On lâche rien ! Pense à la pile d'énergie qui t'attend à la fin !",
  "T'as vu ces muscles ? Fais gaffe, tu vas craquer ton t-shirt !",
  "Bien joué ! Maintenant, on va manger ? J'ai faim moi !",
  "Regarde-moi cette forme olympique ! T'es prêt pour l'Arène !"
];

// --- PROGRAMME SPORT ---
const PROGRAMME_SPORT = {
  1: { 
    title: "Haut du corps & Abdos",
    subtitle: "Focus Haltères",
    exercises: [
      { name: "Shoulder Press (Haltères)", type: "reps", target: "4 x 10-12", volume: 48, desc: "Assis/debout. Pousse les haltères au-dessus de la tête.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Shoulder+Press" },
      { name: "Rowing un bras", type: "reps", target: "4 x 12 / côté", volume: 48, desc: "Genou sur banc, dos plat. Tire le coude vers le haut.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Rowing+Un+Bras" },
      { name: "Curl Biceps", type: "reps", target: "3 x 12", volume: 36, desc: "Coudes serrés au corps. Contracte les biceps.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Curl+Biceps" },
      { name: "Triceps derrière la tête", type: "reps", target: "3 x 12", volume: 36, desc: "Haltère à deux mains derrière la nuque. Extension vers le ciel.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Triceps+Extension" },
      { name: "Planche", type: "time", target: "3 x 45-60 sec", volume: 3, desc: "Gainage statique sur les avant-bras.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Planche" }, 
      { name: "Relevés de jambes", type: "reps", target: "3 x 12-15", volume: 45, desc: "Dos au sol, lève les jambes tendues.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Releves+Jambes" }
    ]
  },
  2: { 
    title: "Bas du corps & Gainage",
    subtitle: "Haltères Lourds",
    exercises: [
      { name: "Squat Haltères", type: "reps", target: "4 x 12", volume: 48, desc: "Haltères aux épaules ou mains. Fesses en arrière, dos droit.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Squat+Halteres" },
      { name: "Fentes Haltères", type: "reps", target: "3 x 12 / jambe", volume: 36, desc: "Grand pas en avant, genou arrière frôle le sol.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Fentes" },
      { name: "Soulevé de terre (Jambes tendues)", type: "reps", target: "3 x 12", volume: 36, desc: "Dos plat, penche-toi en avant, sens l'étirement des ischios.", img: "https://placehold.co/600x300/e2e8f0/475569?text=RDL" },
      { name: "Hip Thrust Haltères", type: "reps", target: "3 x 15", volume: 45, desc: "Dos sur banc, poids sur le bassin. Monte les fesses.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Hip+Thrust" },
      { name: "Planche Latérale", type: "time", target: "2 x 30-40 sec / côté", volume: 2, desc: "Sur le côté, bassin haut et aligné.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Side+Plank" }
    ]
  },
  3: { 
    title: "HIIT + Corde",
    subtitle: "Cardio Intensif",
    exercises: [
      { name: "Corde à sauter", type: "cardio", target: "15-20 min", volume: 20, desc: "Rythme soutenu pour faire monter le cardio.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Corde" },
      { name: "Circuit : Burpees", type: "reps", target: "3 tours : 12 reps", volume: 36, desc: "Squat, planche, pompe, saut. Explosif !", img: "https://placehold.co/600x300/e2e8f0/475569?text=Burpees" },
      { name: "Circuit : Mountain Climbers", type: "time", target: "3 tours : 30 sec", volume: 3, desc: "Position pompe, cours sur place.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Climbers" },
      { name: "Circuit : Squat Jumps", type: "reps", target: "3 tours : 12 reps", volume: 36, desc: "Squat + saut à la remontée.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Squat+Jump" },
      { name: "Circuit : Sprint sur place", type: "time", target: "3 tours : 30 sec", volume: 3, desc: "Genoux hauts, à fond !", img: "https://placehold.co/600x300/e2e8f0/475569?text=Sprint" }
    ]
  },
  4: { 
    title: "Full Body Haltères",
    subtitle: "Mobilité & Renfo",
    exercises: [
      { name: "Rowing Haltères", type: "reps", target: "3 x 12", volume: 36, desc: "Buste penché, tire les deux haltères ensemble.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Rowing" },
      { name: "Squat Haltères", type: "reps", target: "3 x 12", volume: 36, desc: "Contrôle bien la descente.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Squat" },
      { name: "Développé Épaules", type: "reps", target: "3 x 10", volume: 30, desc: "Pousse vers le ciel sans cambrer.", img: "https://placehold.co/600x300/e2e8f0/475569?text=Press" },
